$(function () {
  $(".form-element").submit(function () {
    return (
      $.ajax({
        type: "POST",
        url: "telegram.php",
        data: $(this).serialize(),
      }).done(function () {
        $(this).find("input").val(""),
          window.location.replace("/thank-you.html"),
          $(".form-element").trigger("reset");
      }),
      !1
    );
  }),
    $(".form-docs").submit(function () {
      return (
        $.ajax({
          type: "POST",
          url: "telegram-docs.php",
          data: $(this).serialize(),
        }).done(function () {
          $(this).find("input").val(""),
            window.location.replace("/thank-you.html"),
            $(".form-docs").trigger("reset");
        }),
        !1
      );
    });
});
